var Microtext={breakSentence:function(sentence){return sentence.trim().split(" ");}};Microtext.abbrevName=function(expr){var brokenNames=Microtext.breakSentence(expr);if(brokenNames.length>1){return(brokenNames[0]+" "+brokenNames[1].charAt(0)+".");}else{return(brokenNames[0]);}};Microtext.lastName=function(expr){var brokenNames=Microtext.breakSentence(expr);if(brokenNames.length>1){return(brokenNames[brokenNames.length-1]);}else{return("");}};Microtext.getInitials=function(expr){var brokenNames=Microtext.breakSentence(expr);var nameList=[];brokenNames.forEach(function(name){nameList.push(name.charAt(0));});if(nameList.length>1){var abbrevName=nameList.toString().toUpperCase();return abbrevName.replace(/\,/g,". ");}else{return"";}};